import React, { useEffect, useState } from 'react';
import Banner from '../Components/Banner/Banner';
import all_knives from '../Components/Assets/all_knifes';
import Knife from '../Components/Knife/Knife';
import './CSS/Shop.css';

export const Shop = () => {
  const [randomKnives, setRandomKnives] = useState([]);

  useEffect(() => {
    const getRandomKnives = () => {
      let shuffled = all_knives.sort(() => 0.5 - Math.random());
      return shuffled.slice(0, 3);
    };

    setRandomKnives(getRandomKnives());
  }, []);

  return (
    <div className='shop'>
      <Banner />
      <div className='content'>
        <h2>Today's Selection:</h2>
        <div className='selection'>
          {randomKnives.length > 0 && (
            <>
              <div className='knife-small'>
                <Knife id={randomKnives[0].id} name={randomKnives[0].name} image={randomKnives[0].image} knife_price={randomKnives[0].knife_price} />
              </div>
              <div className='knife-large'>
                <Knife id={randomKnives[1].id} name={randomKnives[1].name} image={randomKnives[1].image} knife_price={randomKnives[1].knife_price} />
              </div>
              <div className='knife-small'>
                <Knife id={randomKnives[2].id} name={randomKnives[2].name} image={randomKnives[2].image} knife_price={randomKnives[2].knife_price} />
              </div>
            </>
          )}
        </div>
        <div className='actions'>
          <button className='btn' onClick={() => window.location.href = '/signup'}>Sign Up Now</button>
          <button className='btn' onClick={() => window.location.href = '/sell'}>Sell</button>
        </div>
      </div>
    </div>
  );
};

export default Shop;
